/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable @next/next/no-img-element */
import { NextRequest, NextResponse } from 'next/server'
import { ImageResponse } from 'next/og'
import { DayEnum } from '#utils/day'
import { ServerRuntime } from 'next'

const dayColorMap: Record<DayEnum, string> = {
  [DayEnum.MONDAY]: '#006FEE',
  [DayEnum.TUESDAY]: '#9353D3',
  [DayEnum.WEDNESDAY]: '#17C964',
  [DayEnum.THURSDAY]: '#F31260',
  [DayEnum.FRIDAY]: '#F5A524',
}

const dayTextMap: Record<DayEnum, string> = {
  [DayEnum.MONDAY]: '월요일',
  [DayEnum.TUESDAY]: '화요일',
  [DayEnum.WEDNESDAY]: '수요일',
  [DayEnum.THURSDAY]: '목요일',
  [DayEnum.FRIDAY]: '금요일',
}

export const runtime: ServerRuntime = 'edge'

export async function GET(request: NextRequest) {
  const pretendardMedium = fetch(
    new URL('../../../../public/fonts/Pretendard-Medium.otf', import.meta.url),
  ).then((res) => res.arrayBuffer())

  const week = request.nextUrl.searchParams.get('week')
  const day = request.nextUrl.searchParams.get('day')
  if (!week || !day || !Object.keys(DayEnum).includes(day)) {
    return NextResponse.json('Bad Request', { status: 400 })
  }

  return new ImageResponse(
    (
      <div
        style={{
          width: 1280,
          height: 640,
          background: '#202020',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          flexDirection: 'column',
          gap: 16,
        }}
      >
        <img
          src="data:image/png;base64,"
          width={250}
          height={134}
        />
        <img
          src="data:image/png;base64,"
          width={717}
          height={88}
        />
        <div
          style={{
            display: 'flex',
            color: '#D4D4D8',
            marginTop: 10,
            fontWeight: 500,
            fontSize: 48,
            whiteSpace: 'pre',
            fontFamily: 'Pretendard-Medium',
          }}
        >
          내일배움캠프 Spring {week}주차{' '}
          <span
            style={{
              color: dayColorMap[day as keyof typeof DayEnum],
              display: 'flex',
            }}
          >
            {dayTextMap[day as keyof typeof DayEnum]}
          </span>
        </div>
      </div>
    ),
    {
      width: 1280,
      height: 640,
      status: 200,
      fonts: [
        {
          data: await pretendardMedium,
          name: 'Pretendard-Medium',
          weight: 500,
        },
      ],
    },
  )
}
